import React, { useState } from 'react';
import { Form, Message, Button, Input } from 'semantic-ui-react';
import Data from '../db.json';

const DefaultPage = () => {
  const [error, setError] = useState(null);
  const [questions, setQuestions] = useState([]);

  const { Questions } = Data;

  setQuestions(Questions[Math.floor(Math.random() * Questions.length)]);

  console.log(questions);

  // const inputInputClick = () => {};

  const submitHandler = (event) => {
    event.preventDefault();
    const givenAnswer = event.target.value;
    console.log(event.target);
  };

  return (
    <>
      {Questions &&
        Questions.map((question) => (
          <Form error onSubmit={submitHandler}>
            <Form.Group inline widths={16}>
              <Form.Field>
                <label>{question.question}</label>
                <Input placeholder="your answer" />
              </Form.Field>
              <Button>Submit</Button>
              {/* <Message error header="Wrong!" content="You have 2 more try" />{' '} */}
            </Form.Group>
          </Form>
        ))}
    </>
  );
};

// IMPORT DATA FROM DB

export default DefaultPage;
